tql.tql:
SELECT * FROM sendgrid.send_mail
  WHERE $body=(SELECT {
    'personalizations' : [ {
      'to' : [ {
        'name' : 'Friend of Transposit',
        'email' : @email
      } ],
    } ],
    'subject' : 'Thank You for Visiting Transposit at SIGNAL 2019',
    'content' : [ {
      "type": "text/html",
      "value": @content
    } ],
    'from' : {
      'name' : 'Transposit@SIGNAL2019',
      'email' : 'noreply@transposit.com'
    }
  })

SELECT * FROM airtable.create_record
  WHERE baseId=@baseid		-- Set this in environment variables
  AND table='Emails'
  AND $body=(SELECT {
    'fields' : { email: @email }
  })
